    getStatusBadge(status) {
        const badges = {
            draft: 'badge-secondary',
            pending: 'badge-warning',
            approved: 'badge-info',
            in_progress: 'badge-primary',
            completed: 'badge-success',
            rejected: 'badge-danger',
        };
        return badges[status] || 'badge-secondary';
